              setEditMensaje={setEditMensaje}
              handleEditSave={handleEditSave}
              onFav={() => favPost(item.id)}
              onRefresh={async () => {
                const data = await fetchFeed(1);
                setFeed(Array.isArray(data) ? data : data.posts || []);
